        assert 0 <= i < |writes|;
        assert 0 <= writes[i].0 < s.mem_len;
        assert 0 <= s.num_entry * 2 + 2 < |s.log|;
        s := ghost_tx_write(s, writes[i].0, writes[i].1);

        end_mem := end_mem[writes[i].0 := writes[i].1];

        assert !crashed(s) ==> s.mem[writes[i].0] == writes[i].1;
        i := i + 1;
