    assert ghost_tx_inv(s);
    assert old_mem_equiv(s);

    var (s', c) := ghost_commit_tx(s);
    assert c ==> !crashed(s);

    if (c) {
        assert !crashed(s);
        assert s.mem == end_mem;
    } else {
        var recovered := ghost_recover(s');
        assert recovered.mem == init_mem;
    }
