vc-spec:
method BubbleSort(A: array<int>, n: int)
modifies A;
requires A.Length>=0 && n==A.Length;

vc-code:
{
  var i := 0;
  var j := 0;

  while(i < n-1){
    while(j < n-i-1){
     if(A[j]<A[i]){
       var t := A[j];
       A[j] := A[i];
       A[i] := t;
     } 
     j := j+1;
    }
    i := i+1;
  }
}

